import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { recargaAPI } from '../services/api';
import './Recargas.css';

export default function Recargas() {
  const { usuario } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState('tarjeta'); // tarjeta | codigo
  const [formData, setFormData] = useState({
    monto: '',
    codigo: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [historial, setHistorial] = useState([]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Recarga con Stripe
  const handleRecargaTarjeta = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await recargaAPI.crearRecargaStripe({
        monto: parseFloat(formData.monto),
      });

      // Redirigir a Stripe Checkout
      if (response.data.sessionId) {
        // En producci√≥n, usar Stripe.redirectToCheckout
        setSuccess(`‚úì Sesi√≥n de pago creada. Ref: ${response.data.numeroReferencia}`);
        setFormData({ monto: '', codigo: '' });
      }
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error en la recarga');
    } finally {
      setLoading(false);
    }
  };

  // Canjear c√≥digo
  const handleCanjearCodigo = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await recargaAPI.canjearcoCodigo({
        codigo: formData.codigo.toUpperCase(),
      });

      setSuccess(`‚úì C√≥digo canjeado. +$${response.data.montoAgregado}`);
      setFormData({ monto: '', codigo: '' });
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error canjeando c√≥digo');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="recargas-container">
      <div className="recargas-card">
        <div className="card-header">
          <h2>Recargar Saldo</h2>
          <p>Agrega dinero a tu cuenta</p>
        </div>

        <div className="saldo-actual">
          <span>Saldo disponible:</span>
          <h3>${parseFloat(usuario?.saldo || 0).toFixed(2)}</h3>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <div className="tabs">
          <button
            className={`tab-btn ${activeTab === 'tarjeta' ? 'active' : ''}`}
            onClick={() => setActiveTab('tarjeta')}
          >
            üí≥ Tarjeta de Cr√©dito
          </button>
          <button
            className={`tab-btn ${activeTab === 'codigo' ? 'active' : ''}`}
            onClick={() => setActiveTab('codigo')}
          >
            üéüÔ∏è C√≥digo de Recarga
          </button>
        </div>

        {/* Recarga con Tarjeta */}
        {activeTab === 'tarjeta' && (
          <form onSubmit={handleRecargaTarjeta} className="form-section">
            <div className="form-group">
              <label>Monto a recargar ($)</label>
              <div className="monto-selector">
                {[10, 20, 50, 100, 200, 500].map((monto) => (
                  <button
                    key={monto}
                    type="button"
                    className={`monto-btn ${formData.monto === monto.toString() ? 'selected' : ''}`}
                    onClick={() => setFormData({ ...formData, monto: monto.toString() })}
                  >
                    ${monto}
                  </button>
                ))}
              </div>
              <input
                type="number"
                name="monto"
                value={formData.monto}
                onChange={handleChange}
                placeholder="Otro monto"
                step="0.01"
                min="1"
                className="custom-monto"
              />
            </div>

            <div className="info-box">
              <h4>Informaci√≥n de la recarga</h4>
              <ul>
                <li>‚úì Recarga instant√°nea</li>
                <li>‚úì M√©todos seguros</li>
                <li>‚úì Sin comisi√≥n</li>
                <li>‚úì Datos encriptados</li>
              </ul>
            </div>

            <button
              type="submit"
              disabled={loading || !formData.monto}
              className="btn-submit"
            >
              {loading ? 'Procesando...' : `Recargar $${parseFloat(formData.monto || 0).toFixed(2)}`}
            </button>
          </form>
        )}

        {/* Canjear C√≥digo */}
        {activeTab === 'codigo' && (
          <form onSubmit={handleCanjearCodigo} className="form-section">
            <div className="form-group">
              <label>C√≥digo de Recarga</label>
              <input
                type="text"
                name="codigo"
                value={formData.codigo}
                onChange={handleChange}
                placeholder="Ej: ABC123XYZ456"
                maxLength="20"
              />
            </div>

            <div className="info-box">
              <h4>¬øC√≥mo obtener un c√≥digo?</h4>
              <ul>
                <li>üìß Recibe c√≥digos por promociones</li>
                <li>üéÅ Compra c√≥digos en tiendas asociadas</li>
                <li>üèÜ Gana c√≥digos en promociones especiales</li>
              </ul>
            </div>

            <button
              type="submit"
              disabled={loading || !formData.codigo}
              className="btn-submit"
            >
              {loading ? 'Validando...' : 'Canjear C√≥digo'}
            </button>
          </form>
        )}
      </div>

      {/* Historial */}
      <div className="historial-card">
        <h3>Historial de Recargas</h3>
        <div className="historial-tabla">
          <table>
            <thead>
              <tr>
                <th>Monto</th>
                <th>M√©todo</th>
                <th>Estado</th>
                <th>Referencia</th>
                <th>Fecha</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>-</td>
                <td>-</td>
                <td>-</td>
                <td>-</td>
                <td>-</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
