import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { recargaAPI } from '../services/api';
import './Recargas.css';

// Validaciones de tarjeta
const validateCardNumber = (number) => {
  const cleaned = number.replace(/\D/g, '');
  if (cleaned.length < 13 || cleaned.length > 19) return false;
  
  // Algoritmo de Luhn
  let sum = 0;
  let isEven = false;
  for (let i = cleaned.length - 1; i >= 0; i--) {
    let digit = parseInt(cleaned[i], 10);
    if (isEven) {
      digit *= 2;
      if (digit > 9) digit -= 9;
    }
    sum += digit;
    isEven = !isEven;
  }
  return sum % 10 === 0;
};

const getCardBrand = (number) => {
  const cleaned = number.replace(/\D/g, '');
  if (/^4[0-9]{12}(?:[0-9]{3})?$/.test(cleaned)) return 'Visa';
  if (/^5[1-5][0-9]{14}$/.test(cleaned)) return 'Mastercard';
  if (/^3[47][0-9]{13}$/.test(cleaned)) return 'American Express';
  if (/^6(?:011|5[0-9]{2})[0-9]{12}$/.test(cleaned)) return 'Discover';
  return null;
};

const validateExpiry = (month, year) => {
  const now = new Date();
  const currentYear = now.getFullYear();
  const currentMonth = now.getMonth() + 1;
  
  const expiryYear = parseInt(year, 10);
  const expiryMonth = parseInt(month, 10);
  
  if (expiryYear < currentYear) return false;
  if (expiryYear === currentYear && expiryMonth < currentMonth) return false;
  if (expiryMonth < 1 || expiryMonth > 12) return false;
  
  return true;
};

const validateCVV = (cvv) => {
  return /^\d{3,4}$/.test(cvv.replace(/\D/g, ''));
};

export default function Recargas() {
  const { usuario, setUsuario } = useContext(AuthContext);
  const [activeTab, setActiveTab] = useState('tarjeta'); // tarjeta | codigo
  const [formData, setFormData] = useState({
    monto: '',
    codigo: '',
    // Datos de tarjeta
    numeroTarjeta: '',
    nombreTitular: '',
    mesVencimiento: '',
    anoVencimiento: '',
    cvv: '',
    tipoTarjeta: 'credito', // credito | debito | ahorros
  });
  const [cardData, setCardData] = useState({
    brand: null,
    isValid: false,
    cardType: 'credito',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    // Validaciones de tarjeta en tiempo real
    if (name === 'numeroTarjeta') {
      const cleaned = value.replace(/\D/g, '');
      const brand = getCardBrand(value);
      const isValid = validateCardNumber(value);
      setCardData({
        brand,
        isValid,
        cardType: formData.tipoTarjeta,
      });
    }
  };

  const handleRecargaTarjeta = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validaciones
    if (!formData.monto || parseFloat(formData.monto) <= 0) {
      setError('Ingresa un monto v√°lido');
      return;
    }

    if (!validateCardNumber(formData.numeroTarjeta)) {
      setError('N√∫mero de tarjeta inv√°lido');
      return;
    }

    if (!validateExpiry(formData.mesVencimiento, formData.anoVencimiento)) {
      setError('Fecha de vencimiento inv√°lida o expirada');
      return;
    }

    if (!validateCVV(formData.cvv)) {
      setError('CVV debe tener 3 o 4 d√≠gitos');
      return;
    }

    if (!formData.nombreTitular.trim()) {
      setError('Nombre del titular requerido');
      return;
    }

    setLoading(true);

    try {
      const response = await recargaAPI.procesarRecargaTarjeta({
        monto: parseFloat(formData.monto),
        numeroTarjeta: formData.numeroTarjeta.replace(/\D/g, ''),
        nombreTitular: formData.nombreTitular,
        mesVencimiento: formData.mesVencimiento.padStart(2, '0'),
        anoVencimiento: formData.anoVencimiento,
        cvv: formData.cvv,
        tipoTarjeta: formData.tipoTarjeta,
        brand: cardData.brand,
      });

      setSuccess(`‚úì Recarga exitosa. +$${response.data.montoAgregado}. Ref: ${response.data.numeroReferencia}`);
      
      // Actualizar usuario
      if (setUsuario) {
        setUsuario(prev => ({
          ...prev,
          saldo: response.data.nuevoSaldo,
        }));
      }

      setFormData({
        monto: '',
        codigo: '',
        numeroTarjeta: '',
        nombreTitular: '',
        mesVencimiento: '',
        anoVencimiento: '',
        cvv: '',
        tipoTarjeta: 'credito',
      });
      setCardData({ brand: null, isValid: false, cardType: 'credito' });
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error procesando recarga');
    } finally {
      setLoading(false);
    }
  };

  // Canjear c√≥digo
  const handleCanjearCodigo = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await recargaAPI.canjearcoCodigo({
        codigo: formData.codigo.toUpperCase(),
      });

      setSuccess(`‚úì C√≥digo canjeado. +$${response.data.montoAgregado}`);
      setFormData({ monto: '', codigo: '' });
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error canjeando c√≥digo');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="recargas-container">
      <div className="recargas-card">
        <div className="card-header">
          <h2>Recargar Saldo</h2>
          <p>Agrega dinero a tu cuenta</p>
        </div>

        <div className="saldo-actual">
          <span>Saldo disponible:</span>
          <h3>${parseFloat(usuario?.saldo || 0).toFixed(2)}</h3>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <div className="tabs">
          <button
            className={`tab-btn ${activeTab === 'tarjeta' ? 'active' : ''}`}
            onClick={() => setActiveTab('tarjeta')}
          >
            üí≥ Tarjeta de Cr√©dito
          </button>
          <button
            className={`tab-btn ${activeTab === 'codigo' ? 'active' : ''}`}
            onClick={() => setActiveTab('codigo')}
          >
            üéüÔ∏è C√≥digo de Recarga
          </button>
        </div>

        {/* Recarga con Tarjeta */}
        {activeTab === 'tarjeta' && (
          <form onSubmit={handleRecargaTarjeta} className="form-section">
            <div className="form-group">
              <label>Monto a recargar ($)</label>
              <div className="monto-selector">
                {[10, 20, 50, 100, 200, 500].map((monto) => (
                  <button
                    key={monto}
                    type="button"
                    className={`monto-btn ${formData.monto === monto.toString() ? 'selected' : ''}`}
                    onClick={() => setFormData({ ...formData, monto: monto.toString() })}
                  >
                    ${monto}
                  </button>
                ))}
              </div>
              <input
                type="number"
                name="monto"
                value={formData.monto}
                onChange={handleChange}
                placeholder="Otro monto"
                step="0.01"
                min="1"
                className="custom-monto"
              />
            </div>

            <div className="info-box">
              <h4>Informaci√≥n de la recarga</h4>
              <ul>
                <li>‚úì Recarga instant√°nea</li>
                <li>‚úì M√©todos seguros</li>
                <li>‚úì Sin comisi√≥n</li>
                <li>‚úì Datos encriptados</li>
              </ul>
            </div>

            <button
              type="submit"
              disabled={loading || !formData.monto}
              className="btn-submit"
            >
              {loading ? 'Procesando...' : `Recargar $${parseFloat(formData.monto || 0).toFixed(2)}`}
            </button>
          </form>
        )}

        {/* Canjear C√≥digo */}
        {activeTab === 'codigo' && (
          <form onSubmit={handleCanjearCodigo} className="form-section">
            <div className="form-group">
              <label>C√≥digo de Recarga</label>
              <input
                type="text"
                name="codigo"
                value={formData.codigo}
                onChange={handleChange}
                placeholder="Ej: ABC123XYZ456"
                maxLength="20"
              />
            </div>

            <div className="info-box">
              <h4>¬øC√≥mo obtener un c√≥digo?</h4>
              <ul>
                <li>üìß Recibe c√≥digos por promociones</li>
                <li>üéÅ Compra c√≥digos en tiendas asociadas</li>
                <li>üèÜ Gana c√≥digos en promociones especiales</li>
              </ul>
            </div>

            <button
              type="submit"
              disabled={loading || !formData.codigo}
              className="btn-submit"
            >
              {loading ? 'Validando...' : 'Canjear C√≥digo'}
            </button>
          </form>
        )}
      </div>

      {/* Historial */}
      <div className="historial-card">
        <h3>Historial de Recargas</h3>
        <div className="historial-tabla">
          <table>
            <thead>
              <tr>
                <th>Monto</th>
                <th>M√©todo</th>
                <th>Estado</th>
                <th>Referencia</th>
                <th>Fecha</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>-</td>
                <td>-</td>
                <td>-</td>
                <td>-</td>
                <td>-</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
