import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { bankAccountAPI } from '../services/api';
import './VincularCuenta.css';

export default function VincularCuenta() {
  const { usuario } = useContext(AuthContext);
  const [tab, setTab] = useState('vincular'); // vincular | verificar | listado
  const [formData, setFormData] = useState({
    nombreCuenta: '',
    numeroCuenta: '',
    banco: 'Banco Barenvas',
    tipoCuenta: 'ahorros',
    ruteo: '', // Routing number
  });
  const [verificacion, setVerificacion] = useState({
    cuentaId: '',
    deposit1: '',
    deposit2: '',
  });
  const [cuentas, setCuentas] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    if (tab === 'listado') {
      cargarCuentas();
    }
  }, [tab]);

  const cargarCuentas = async () => {
    try {
      const response = await bankAccountAPI.listarCuentas();
      setCuentas(response.data);
    } catch (err) {
      console.error('Error cargando cuentas', err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleVerificacionChange = (e) => {
    const { name, value } = e.target;
    setVerificacion({
      ...verificacion,
      [name]: value,
    });
  };

  const handleVincular = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await bankAccountAPI.vincularCuenta(formData);
      setSuccess('‚úì Cuenta vinculada. Esperando microdeposits...');
      setFormData({
        nombreCuenta: '',
        numeroCuenta: '',
        banco: 'Banco Barenvas',
        tipoCuenta: 'ahorros',
        ruteo: '',
      });
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error vinculando cuenta');
    } finally {
      setLoading(false);
    }
  };

  const handleVerificar = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await bankAccountAPI.verificarCuenta(verificacion);
      setSuccess('‚úì Cuenta verificada exitosamente');
      setVerificacion({ cuentaId: '', deposit1: '', deposit2: '' });
      cargarCuentas();
    } catch (err) {
      setError(err.response?.data?.mensaje || 'Error verificando cuenta');
    } finally {
      setLoading(false);
    }
  };

  const handleDesvincular = async (cuentaId) => {
    if (window.confirm('¬øDesvincular esta cuenta?')) {
      try {
        await bankAccountAPI.desvincularCuenta(cuentaId);
        setCuentas(cuentas.filter((c) => c.id !== cuentaId));
        setSuccess('‚úì Cuenta desvinculada');
      } catch (err) {
        setError(err.response?.data?.mensaje || 'Error desvinculando');
      }
    }
  };

  return (
    <div className="vincular-container">
      <div className="vincular-card">
        <div className="card-header">
          <h2>Gestionar Cuentas Bancarias</h2>
          <p>Vincula tu cuenta bancaria para recargas directas</p>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <div className="tabs">
          <button
            className={`tab-btn ${tab === 'vincular' ? 'active' : ''}`}
            onClick={() => setTab('vincular')}
          >
            ‚ûï Vincular Cuenta
          </button>
          <button
            className={`tab-btn ${tab === 'verificar' ? 'active' : ''}`}
            onClick={() => setTab('verificar')}
          >
            ‚úì Verificar
          </button>
          <button
            className={`tab-btn ${tab === 'listado' ? 'active' : ''}`}
            onClick={() => setTab('listado')}
          >
            üìã Mis Cuentas
          </button>
        </div>

        {/* Vincular */}
        {tab === 'vincular' && (
          <form onSubmit={handleVincular} className="form-section">
            <div className="form-row">
              <div className="form-group">
                <label>Nombre del Titular</label>
                <input
                  type="text"
                  name="nombreCuenta"
                  value={formData.nombreCuenta}
                  onChange={handleChange}
                  placeholder="Juan P√©rez"
                  required
                />
              </div>

              <div className="form-group">
                <label>N√∫mero de Cuenta</label>
                <input
                  type="text"
                  name="numeroCuenta"
                  value={formData.numeroCuenta}
                  onChange={handleChange}
                  placeholder="1234567890"
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Banco</label>
                <select
                  name="banco"
                  value={formData.banco}
                  onChange={handleChange}
                  required
                >
                  <option value="Banco Barenvas">Banco Barenvas</option>
                  <option value="Banco Mercantil">Banco Mercantil</option>
                  <option value="Banco Provincial">Banco Provincial</option>
                  <option value="Banco de Venezuela">Banco de Venezuela</option>
                  <option value="BBVA">BBVA</option>
                </select>
              </div>

              <div className="form-group">
                <label>Tipo de Cuenta</label>
                <select
                  name="tipoCuenta"
                  value={formData.tipoCuenta}
                  onChange={handleChange}
                  required
                >
                  <option value="ahorros">Ahorros</option>
                  <option value="corriente">Corriente</option>
                </select>
              </div>
            </div>

            <div className="form-group">
              <label>Routing Number</label>
              <input
                type="text"
                name="ruteo"
                value={formData.ruteo}
                onChange={handleChange}
                placeholder="Ej: 021000021"
                required
              />
            </div>

            <div className="info-box">
              <h4>‚ö†Ô∏è Seguridad</h4>
              <ul>
                <li>‚úì Datos encriptados con AES-256</li>
                <li>‚úì Verificaci√≥n con microdeposits</li>
                <li>‚úì Cuentas protegidas por Stripe</li>
              </ul>
            </div>

            <button type="submit" disabled={loading} className="btn-submit">
              {loading ? 'Procesando...' : 'Vincular Cuenta'}
            </button>
          </form>
        )}

        {/* Verificar */}
        {tab === 'verificar' && (
          <form onSubmit={handleVerificar} className="form-section">
            <div className="form-group">
              <label>Seleccionar Cuenta a Verificar</label>
              <select
                name="cuentaId"
                value={verificacion.cuentaId}
                onChange={handleVerificacionChange}
                required
              >
                <option value="">-- Selecciona una cuenta --</option>
                {cuentas
                  .filter((c) => c.estado === 'pendiente')
                  .map((c) => (
                    <option key={c.id} value={c.id}>
                      {c.nombreCuenta} - {c.banco} ({c.numerosCuenta})
                    </option>
                  ))}
              </select>
            </div>

            <div className="info-box">
              <h4>üìß Microdeposits Recibidos</h4>
              <p>Stripe habr√° enviado 2 dep√≥sitos peque√±os a tu cuenta en 1-2 d√≠as h√°biles.</p>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Primer Dep√≥sito ($)</label>
                <input
                  type="number"
                  name="deposit1"
                  value={verificacion.deposit1}
                  onChange={handleVerificacionChange}
                  placeholder="0.01"
                  step="0.01"
                  min="0.01"
                  max="0.99"
                  required
                />
              </div>

              <div className="form-group">
                <label>Segundo Dep√≥sito ($)</label>
                <input
                  type="number"
                  name="deposit2"
                  value={verificacion.deposit2}
                  onChange={handleVerificacionChange}
                  placeholder="0.01"
                  step="0.01"
                  min="0.01"
                  max="0.99"
                  required
                />
              </div>
            </div>

            <button type="submit" disabled={loading} className="btn-submit">
              {loading ? 'Verificando...' : 'Verificar Cuenta'}
            </button>
          </form>
        )}

        {/* Listado */}
        {tab === 'listado' && (
          <div className="form-section">
            <h3>Tus Cuentas Bancarias</h3>
            {cuentas.length === 0 ? (
              <p className="sin-cuentas">No tienes cuentas vinculadas</p>
            ) : (
              <div className="cuentas-lista">
                {cuentas.map((cuenta) => (
                  <div key={cuenta.id} className="cuenta-item">
                    <div className="cuenta-info">
                      <h4>{cuenta.nombreCuenta}</h4>
                      <p>{cuenta.banco}</p>
                      <p className="cuenta-numero">****{cuenta.numerosCuenta}</p>
                    </div>
                    <div className="cuenta-estado">
                      <span className={`badge badge-${cuenta.estado}`}>
                        {cuenta.estado}
                      </span>
                      {cuenta.esDefault && <span className="badge badge-default">Por defecto</span>}
                    </div>
                    <div className="cuenta-acciones">
                      {cuenta.estado === 'verificada' && !cuenta.esDefault && (
                        <button
                          className="btn-peque√±o btn-default"
                          onClick={() => alert('Funci√≥n establecer default por hacer')}
                        >
                          Establecer como Default
                        </button>
                      )}
                      <button
                        className="btn-peque√±o btn-danger"
                        onClick={() => handleDesvincular(cuenta.id)}
                      >
                        Desvincular
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
